import { Card } from 'antd';
import UpdatePerson from '../forms/UpdatePerson';
import RemovePerson from '../buttons/RemovePerson';
import CarCard from './CarCard';
import { EditOutlined } from '@ant-design/icons';
import { useState } from 'react';

const ContactCard = ({ id, firstName, lastName, cars = [] }) => {
  const [editMode, setEditMode] = useState(false);
  const styles = getStyles();

  const handleButtonClick = () => {
    setEditMode(!editMode);
  };

  return (
    <div>
      {editMode ? (
        <UpdatePerson id={id} firstName={firstName} lastName={lastName} onButtonClick={handleButtonClick} />
      ) : (
        <Card
          title={`${firstName} ${lastName}`}
          style={styles.card}
          actions={[
            <EditOutlined onClick={handleButtonClick} />,
            <RemovePerson id={id} />
          ]}
        >
          {Array.isArray(cars) && cars.length > 0 ? ( // ðŸ”¹ VerificaciÃ³n doble para evitar errores
            cars.map(car => (
              <CarCard
                key={car.id}
                id={car.id}
                year={car.year}
                make={car.make}
                model={car.model}
                price={car.price}
                owner={{ id, firstName, lastName }}
              />
            ))
          ) : (
            <p>No cars available</p>
          )}
        </Card>
      )}
    </div>
  );
};

const getStyles = () => ({
  card: {
    width: '600px',
    marginBottom: '20px'
  }
});

export default ContactCard;
