import { Card } from 'antd';
import UpdateCar from '../forms/UpdateCar';
import UpdatePerson from '../forms/UpdatePerson';
import RemoveCar from '../buttons/RemoveCar';
import RemovePerson from '../buttons/RemovePerson';
import { EditOutlined, DeleteOutlined } from '@ant-design/icons';
import { useState } from 'react';

const CarCard = ({ owner, cars = [] }) => {  // ✅ Agregamos un valor por defecto a cars
  const [editOwner, setEditOwner] = useState(false);
  const styles = getStyles();

  const handleOwnerEditClick = () => {
    setEditOwner(!editOwner);
  };

  return (
    <Card
      style={styles.card}
      title={
        editOwner ? (
          <UpdatePerson
            id={owner?.id}  // ✅ Validamos owner
            firstName={owner?.firstName}
            lastName={owner?.lastName}
            onButtonClick={handleOwnerEditClick}
          />
        ) : (
          <>
            {owner?.firstName} {owner?.lastName}
            <EditOutlined onClick={handleOwnerEditClick} style={{ marginLeft: 10, marginRight: 10 }} />
            <DeleteOutlined onClick={() => RemovePerson({ id: owner?.id })} style={{ color: 'red' }} />
          </>
        )
      }
    >
      {cars.length > 0 ? (
        cars.map((car) => (
          <Card
            key={car.id}
            style={styles.innerCard}
            title={`${car.make} ${car.model} (${car.year})`}
            extra={`$${car.price}`}
            actions={[
              <EditOutlined onClick={() => console.log(`Edit Car ${car.id}`)} />, // ✅ Evitamos errores con setEditMode
              <RemoveCar id={car.id} />
            ]}
          >
            <p><strong>Price:</strong> ${car.price}</p>
          </Card>
        ))
      ) : (
        <p>No cars available</p>
      )}
    </Card>
  );
};

const getStyles = () => ({
  card: {
    width: '600px',
    marginBottom: '20px'
  },
  innerCard: {
    marginBottom: '10px'
  }
});

export default CarCard;
