import Card from 'antd/es/card/Card';
import UpdateCar from '../forms/UpdateCar';
import RemoveCar from '../buttons/RemoveCar';
import { EditOutlined } from '@ant-design/icons';
import { useState } from 'react';

const CarCard = props => {
  const [editMode, setEditMode] = useState(false);
  const styles = getStyles();
  const { id, year, make, model, price, owner } = props;

  const handleButtonClick = () => {
    setEditMode(!editMode);
  };

  return (
    <div>
      {editMode ? (
        <UpdateCar
          id={id}
          year={year}
          make={make}
          model={model}
          price={price}
          personId={owner.id}
          onButtonClick={handleButtonClick}
        />
      ) : (
        <Card
          style={styles.card}
          title={`${owner.firstName} ${owner.lastName}`} // Ahora el nombre del propietario es el título
          extra={
            <span>
              <strong>Price:</strong> ${price}
            </span>
          }
          actions={[
            <EditOutlined id={id} onClick={handleButtonClick} />,
            <RemoveCar id={id} />
          ]}
        >
          <p><strong>Car:</strong> {make} {model} ({year})</p>
        </Card>
      )}
    </div>
  );
};

const getStyles = () => ({
  card: {
    width: '500px',
    marginBottom: '15px'
  }
});

export default CarCard;
